```cpp
#include <bits/stdc++.h>
using namespace std;

int main() {
    string s;
    cin >> s;
    int n = s.length();
    bool isPalindrome = true;
    for (int i = 0; i < n / 2; i++) {
        if (s[i] != s[n - i - 1]) {
            isPalindrome = false;
            break;
        }
    }
    if (isPalindrome) cout << "YES" << endl;
    else cout << "NO" << endl;
    return 0;
}
```
