```ts
/**
 * Actions de configuration
 */
export interface SettingsAction {
  /**
   * Réinitialiser les paramètres
   */
  resetSettings: () => void;
  /**
   * Définir les paramètres globaux
   * @param settings - Paramètres globaux
   */
  setGlobalSettings: (settings: GlobalSettingsState) => void;
  /**
   * Définir partiellement les paramètres
   * @param settings - Paramètres partiels
   */
  setSettings: (settings: Partial<ConfigSettings>) => void;
  /**
   * Définir le mode thème
   * @param themeMode - Mode thème
   */
  setThemeMode: (themeMode: ThemeMode) => void;
  /**
   * Basculer l'onglet de la barre latérale
   * @param key - Onglet sélectionné de la barre latérale
   */
  switchSideBar: (key: SidebarTabKey) => void;
}
```
